import React from "react";
// javascript plugin used to create scrollbars on windows
import PerfectScrollbar from "perfect-scrollbar";
import { Route, Switch } from "react-router-dom";
import { userContext } from 'views/Logincontext'
import DemoNavbar from "components/Navbars/DemoNavbar.jsx";
import Footer from "components/Footer/Footer.jsx";
import Sidebar from "components/Sidebar/Sidebar.jsx";

import route from "routes.js";
var ps;
var menuroute =route.filter(function (entry) { return entry.display === true; });

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      backgroundColor: "black",
      activeColor: "info"
    };
    this.mainPanel = React.createRef();
  }
  static contextType = userContext;

  componentDidMount() {
    console.log("hi1")
    const { user } = this.context

    let obj = user;
    let keys = Object.keys(obj);
    let name = obj[keys[1]];
    let adminOruser = obj[keys[5]];
    console.log(adminOruser)
    if (adminOruser === "guest") {
      menuroute = route.filter(function (entry) { return entry.display === true; });
    } else if (adminOruser === "Y") {
      menuroute = route.filter(function (entry) { return entry.display === "admin" || entry.display === true || entry.display === "both" });
    } else if (adminOruser === "N") {
      menuroute = route.filter(function (entry) { return entry.display === "user" || entry.display === true || entry.display === "both" });
    }
    console.log(menuroute)
    console.log(route)
    if (navigator.platform.indexOf("Win") > -1) {
      ps = new PerfectScrollbar(this.mainPanel.current);
      document.body.classList.toggle("perfect-scrollbar-on");
    }
  }
  componentWillUnmount() {
    console.log("hi2")
    if (navigator.platform.indexOf("Win") > -1) {
      ps.destroy();
      document.body.classList.toggle("perfect-scrollbar-on");
    }
  }
  componentDidUpdate(e) {
    const { user } = this.context
    console.log("hi3")
    let obj = user;
    let keys = Object.keys(obj);
    let name = obj[keys[1]];
    let adminOruser = obj[keys[5]];
    console.log(adminOruser)
    if (adminOruser === "guest") {
      menuroute = route.filter(function (entry) { return entry.display === true; });
    } else if (adminOruser === "Y") {
      menuroute = route.filter(function (entry) { return entry.display === "admin" || entry.display === "both" });
    } else if (adminOruser === "N") {
      menuroute = route.filter(function (entry) { return entry.display === "user" || entry.display === "both" });
    }
    console.log(menuroute)
    console.log(route)

    if (e.history.action === "PUSH") {
      this.mainPanel.current.scrollTop = 0;
      document.scrollingElement.scrollTop = 0;
    }
  }
  handleActiveClick = color => {
    this.setState({ activeColor: color });
  };
  handleBgClick = color => {
    this.setState({ backgroundColor: color });
  };
  render() {
    if(menuroute=== undefined){
      return (
        <div className="wrapper">
          <Sidebar
            {...this.props}
            routes={route}
            bgColor={this.state.backgroundColor}
            activeColor={this.state.activeColor}
          />
          <div className="main-panel" ref={this.mainPanel}>
            <DemoNavbar {...this.props} />
            <Switch>
              {route.map((prop, key) => {
                return (
                  <Route
                    path={prop.layout + prop.path}
                    component={prop.component}
                    key={key}
                  />
                );
              })}
            </Switch>
            <Footer fluid />
          </div>
  
        </div>
      );
    }else
    {
    return (
      <div className="wrapper">
        <Sidebar
          {...this.props}
          routes={menuroute}
          bgColor={this.state.backgroundColor}
          activeColor={this.state.activeColor}
        />
        <div className="main-panel" ref={this.mainPanel}>
          <DemoNavbar {...this.props} />
          <Switch>
            {menuroute.map((prop, key) => {
              return (
                <Route
                  path={prop.layout + prop.path}
                  component={prop.component}
                  key={key}
                />
              );
            })}
          </Switch>
          <Footer fluid />
        </div>

      </div>
    );
  }
}
}

export default Dashboard;
