import React from 'react';
import Grid from '@material-ui/core/Grid';
class AddQuestion extends React.Component {
    state = {
        MCQ_option: [],
        MCQ_ques: [],
        MCQ_Answer: [],
        Ques_id: Math.random()
    }

    handleEvent = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    handleSumbmitEvent = (e) => {

        e.preventDefault();
        var data1 = "";
        if (this.state.MCQ_option !== "" && this.state.MCQ_ques !== "") {
            var optionid = this.state.MCQ_option.split(',');
            var MCQ_queslist = ''
            // let jsonObj = []
            // let itemnews = []
            // let itemnew = {}
            // let jsonnewObj = [];
            // let jsonnewObj2 = []
            for (var i = 0; i < optionid.length; i++) {
                MCQ_queslist = MCQ_queslist + ',' + '"' + optionid[i] + '"'
                // jsonObj.push(optionid[i])
            }
            // itemnews["option"] =jsonObj;
            // jsonnewObj2.push(itemnews)
            // console.log(jsonnewObj2)
            // console.log(JSON.stringify(jsonObj))
            // itemnew["id"]= Math.random()
            // itemnew["Ques_id"] = this.state.Ques_id ;
            // itemnew["MCQ_Answer"]=this.state.MCQ_Answer;
            // itemnew["MCQ_ques"] = this.state.MCQ_ques;
            // itemnew["MCQ_option"] = jsonnewObj2;
            // jsonnewObj.push(itemnew);
            // console.log(jsonnewObj)
            var tempDate = new Date();
            var date = tempDate.getFullYear() + '-' + (tempDate.getMonth() + 1) + '-' + tempDate.getDate() + ' ' + tempDate.getHours() + ':' + tempDate.getMinutes() + ':' + tempDate.getSeconds();

            data1 = '{"Ques_id":' + '"' + this.state.Ques_id + '"' + ',"MCQ_Answer":' + '"' + this.state.MCQ_Answer + '"' + ',"MCQ_ques":' + '"' + this.state.MCQ_ques + '"' + ',"MCQ_option":{"option":[ ' + MCQ_queslist.substr(1) + ']}' + ',"Inserted_date":' + '"' + date + '"}';
            //data1 = '{"Ques_id":' + '"'+this.state.Ques_id+'"'+ ',"MCQ_Answer":' + '"'+this.state.MCQ_Answer+'"'+ ',"MCQ_ques":' + '"'+this.state.MCQ_ques+'"'+ ',"User_id":' +0+ ',"Result":' + '"'+"null"+'"'+ ',"MCQ_option":{"option":[ '+ MCQ_queslist.substr(1) + ']}}';

            console.log(data1)
            this.props.AddDetails(JSON.parse(data1))
            e.target.reset();
            this.setState({
                MCQ_option: "",
                MCQ_ques: "",
                MCQ_Answer: ""
            })
        }
    }

    render() {
        return (
            <form onSubmit={this.handleSumbmitEvent}>
                <label>Question
                <textarea type="text" id="MCQ_ques" onChange={this.handleEvent}></textarea></label>
                <label>Option
                <textarea type="text" id="MCQ_option" onChange={this.handleEvent}></textarea></label>
                <label>Answer
                <textarea type="text" id="MCQ_Answer" onChange={this.handleEvent}></textarea></label>
                <button type="submit">Add Ques. </button>
            </form>
        )
    }
}

export default AddQuestion;
