import React, { Component } from 'react';
export default class signin extends Component {
    state =
        {
            email: "",
            password: "",
            Userdetails: 0
        }
    // const [username, setusername]= useState(props.location.state.id)
    handleEvent = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    handleSubmit(event) {
        event.preventDefault();
        const newUser = {
            Useremail: "vasu.tariyal@gmail.com",
            Userpassword: "vasundhra"
        }

        fetch('/users/login', {
            method: 'POST',
            body: JSON.stringify(newUser),
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(res => res.json())
            .then(Userdetails => this.setState({ Userdetails }))
            .catch(error => console.error('Error:', error));


    }
    render() {
        return (

            <form onSubmit={this.handleSubmit}>
                <div className="container">

                    <div>
                        <label>email
                <textarea type="text" id="email" onChange={this.handleEvent}></textarea></label>
                        <label>password
                <textarea type="text" id="password" onChange={this.handleEvent}></textarea></label>
                        <button type="submit">submit. </button>
                    </div>
                </div>
            </form>



        )
    }
}
