import React, { useState } from 'react';
export default class Uploadfile extends React.Component {
    state = {
        files: ""
    }
    componentDidMount() {
        const newUser = { Ques_id: this.props.location.state.id }
            fetch('/uploadfile_api/uploadfile')
            .then(res => res.json())
            .then(files => this.setState({ files }));
        render()
        {
            return (
                <div>
                    <div class="hero-body">
                        <div class="container has-text-centered">
                            <h1 class="title"> Test File Uploads</h1>
                            <form action="/upload" method="POST" enctype="multipart/form-data">
                                <div class="file is-centered">
                                    <label class="file-label">
                                        <input class="file-input" type="file" name="file" id="file" />
                                        <span class="file-cta">
                                            <span class="file-icon"><i class="fas fa-upload"></i></span>
                                            <span class="file-label">Choose a file…</span>
                                        </span>
                                    </label>
                                </div>
                                <input class="button is-link" type="submit" value="Submit" />
                            </form>
                        </div>
                    </div>

            if(files) {
                        files.forEach(function (file) {
                            <div class="columns is-mobile">
                                <div class="column is-half is-offset-one-quarter">
                                    if(file.isImage) {
                                        <div class="image">
                                            <img src="image/<%= file.filename" alt="Placeholder image" />
                                            <div> file.filename </div>
                                        </div>
                                    } else {
                                        <form method="GET" action="/files/<%= file.filename ?_method=GET">
                                            <button class="button is-link">
                                                <span class="icon is-offset-one-quarter"><i class="fas file-icon"></i></span>
                                                <span>Download File</span>
                                            </button>
                                        </form>

                                    }
                                    <form method="POST" action="/files/<%= file._id ?_method=DELETE">
                                        <button class="button is-danger">
                                            <span class="icon is-small"><i class="fas fa-trash-alt"></i></span><span>Delete</span>
                                        </button>
                                    </form>
                                </div>
                            </div>
                        })
                    } else {
                        <p class="content">No files to show</p>
                    }
                </div>
            )
        }
    }
}
