import React , { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { Redirect } from 'react-router-dom';
import {userContext} from 'views/Logincontext'

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles(theme => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default class SignIn extends React.Component{
 state={
  email:"",
  password:"",
  Userdetails:0
}
  static contextType = userContext;

  render(){
    const {updateState } = this.context
 // const [username, setusername]= useState(props.location.state.id)
  function validateForm() {
    return this.state.email.length > 0 && this.state.password.length > 0;
  }
  function handleSubmit(event) {
    event.preventDefault();
    const newUser={
      Useremail:this.state.email,
      Userpassword:this.state.password
    }
    function handleEvent (e) {
      this.setState({
          [e.target.id]: e.target.value
      })
  }
  
    fetch('/users/login', {
      method: 'POST',
      body: JSON.stringify(newUser),
      headers: {
          'Content-Type': 'application/json'
      }}).then(res => res.json())
      .then(Userdetails => this.setState({ Userdetails:Userdetails }))
      .catch(error => console.error('Error:', error));
  
      updateState(this.state.Userdetails)
  }
  
  if(this.state.Userdetails!==0){
  let obj = this.state.Userdetails;
      let keys = Object.keys(obj);
     let lat = obj[keys[0]];
    

  if (String(lat.UserAdmin) === 'N') {
    return <Redirect to={{
      pathname: '/admin/AdminTestPaperPage',
      state: { Name: lat }
  }}
/>
  }else if (String(lat.UserAdmin) === 'Y') {
    return <Redirect to={{
      pathname: '/admin/AdminPage',
      state: { Name: lat }
  }}
/>
  }}
  else{
  return (
   // <div style={{backgroundImage: `url(${homeimg})`}}>
   <userContext.Provider value={this.state.Userdetails}> 
    <Container component="main" maxWidth="xs" style={{background:"#FFFDD0"}}>
      <CssBaseline />
      <div className={useStyles.paper}>
        <Avatar className={useStyles.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign in
        </Typography>
        <form onSubmit={handleSubmit} className={useStyles.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            autoFocus
            onChange={this.handleEvent}
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
            onChange={this.handleEvent}
          />
          <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={useStyles.submit}
            disabled={!validateForm()}
          >
            Sign In
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              <Link href="/signup/" variant="body2">
                {"Don't have an account? Sign Up"}
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={8}>
        <Copyright />
      </Box>
    </Container>
    </userContext.Provider>
  
  );
}
  }
}
