import React from 'react';
class  AdmitCard extends React.Component {
    state = {
        user_id: "5e7f4a359f828716503c5b39",
        UserStatus: [],
        usersinfo:[]
    }
    componentDidMount() {
        const newUser = {User_id:this.state.user_id};
         fetch('/users/userinfo_byid', {
            method: 'POST',
            body: JSON.stringify(newUser),
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(res => res.json())
            //.then(response => console.log('Success:', JSON.stringify(response)))
            .then(usersinfo => this.setState({ usersinfo }))
            .catch(error => console.error('Error:', error))

        
    }
    render() {
        console.log(this.state.usersinfo)
        const AdmitCard = this.state.usersinfo.map(MCQ_ques => {
            return (
              <div key={MCQ_ques.id}>
                <Grid container>
                  <Grid item xs={3}>
                    <div> Question: {MCQ_ques.MCQ_ques} </div></Grid>
                  
                  <Grid item xs={3}>
                    <div> Answer: {MCQ_ques.MCQ_Answer} </div>
                  </Grid>
                  <Grid item xs={3}>
                    <button onClick={() => { DeleteMCQ_quesdetails(MCQ_ques.id) }}> Delete Item </button>
                  </Grid>
                </Grid>
              </div>
            )
          }
          )
          return (
            <div>
              {AdmitCard}
            </div>
          )
    }
}


export default AdmitCard;
