import React, { Component } from 'react';
export default class signin extends Component {
    state =
        {
            email: "",
            password: "",
            Userdetails: []
        }
    // const [username, setusername]= useState(props.location.state.id)
  

    
    render() {
        handleEvent = (e) => {
            this.setState({
                [e.target.id]: e.target.value
            })
        }
        handleSubmit= (e) => {
            e.preventDefault();
            const newUser = {
                Useremail: "vasu.tariyal@gmail.com",
                Userpassword: "vasundhra"
            }
    
            fetch('/users/login', {
                method: 'POST',
                body: JSON.stringify(newUser),
                headers: {
                    'Content-Type': 'application/json'
                }
            }).then(res => res.json())
            .then(returndata =>this.setState({Userdetails: returndata }))
                .catch(error => console.error('Error:', error));
        console.log(this.state.Userdetails)
        }
        return (

            <form onSubmit={handleSubmit}>
                <div className="container">

                    <div>
                        <label>email
                <textarea type="text" id="email" onChange={handleEvent}></textarea></label>
                        <label>password
                <textarea type="text" id="password" onChange={handleEvent}></textarea></label>
                        <button type="submit">submit. </button>
                    </div>
                </div>
            </form>



        )
    }
}
