import React , { useState , useEffect} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import {userContext} from 'views/Logincontext'

export default class UserContextProvider extends React.Component
{
    state=
    {
    email:"",
    password:"",
    Userdetails:0
}
// const [username, setusername]= useState(props.location.state.id)
handleEvent = (e) => {
    this.setState({
        [e.target.id]: e.target.value
    })
}
   handleSubmit(event) {
  event.preventDefault();
  const newUser={
    Useremail:"vasu.tariyal@gmail.com",
    Userpassword:"vasundhra"
  }

  fetch('/users/login', {
    method: 'POST',
    body: JSON.stringify(newUser),
    headers: {
        'Content-Type': 'application/json'
    }}).then(res => res.json())
    .then(Userdetails => this.setState({ Userdetails }))
    .catch(error => console.error('Error:', error));

 
}
    render()
    {
        return(

<form onSubmit={this.handleSubmit}>
                <div className="container">
                    
                    <div>
                        <label>email
                <textarea type="text" id="email" onChange={this.handleEvent}></textarea></label>
                        <label>password
                <textarea type="text" id="password" onChange={this.handleEvent}></textarea></label>
                         <button type="submit">submit. </button>
                    </div>
                </div>
            </form>


            
        )
    }
}
